import React, {useState}from 'react';
import DishMenus from './DishMenus/DishMenus';

const DISHPRICE = {
    dish1: 1,
    dish2: 2,
    dish3: 3,
    dish4: 4,
    dish5: 5
}
const MenuBoard = () => {
    let [menus, resetMenus] = useState(
        {
            dish1: 0,
            dish2: 0,
            dish3: 0,
            dish4: 0,
            dish5: 0
        }
    )
    let [price, resetPrice] = useState(
        {
            dish1: 0,
            dish2: 0,
            dish3: 0,
            dish4: 0,
            dish5: 0
        }
    )

    const addDishs = (type) => {
        let oldCount = menus[type];
        let newCount = oldCount +1;
        let upDateMenus = {
            ...menus
        }
        upDateMenus[type] = newCount;
        let updatePrice  = {
            ...price
        }
         updatePrice[type] = price[type] + DISHPRICE[type];
        resetMenus(upDateMenus);
        resetPrice(updatePrice);
    }
    const removeDishs = (type) => {
        let oldCount = menus[type];
        let newCount = oldCount -1;
        let upDateMenus = {
            ...menus
        }
        upDateMenus[type] = newCount;
        let updatePrice  = {
            ...price
        }
         updatePrice[type] = price[type] - DISHPRICE[type];
        resetMenus(upDateMenus);
        resetPrice(updatePrice);
    }
    return (
        <div>
            <DishMenus 
            added = {addDishs}
            dishes = {menus}
            price = {price} 
            clickImg = {addDishs} 
            />
        </div>
    )
}
export default MenuBoard;
