import React, {useState} from 'react';
import localServerAxios from "axios";
import Button from '../UI/Button/Button'
import Spinner from '../UI/Spinner/Spinner';
import classes from './ContactData.module.css';
import axios from '../../axios-orders';
import Input from '../UI/Input/Input';

import Row from "./prebuilt/Row";
import BillingDetailsFields from "./prebuilt/BillingDetailsFields";
import SubmitButton from "./prebuilt/SubmitButton";
import CheckoutError from "./prebuilt/CheckoutError";
import styled from "@emotion/styled";
import {CardElement, useElements, useStripe} from '@stripe/react-stripe-js';
// import {Route} from 'react-router-dom';
// import EndPage from '../EndPage/EndePage';
const ContactData = (props) => {
  const [orderForm, setOrderForm] = useState (
      {
        name: {
            elementType: 'input',
            elementConfig: {
                type: 'text',
                placeholder: 'Your Name',
                name: 'name'
            },
            value: '',
            validation: {
                required: true
            },
            valid: false,
            touched: false
        },
        street: {
            elementType: 'input',
            elementConfig: {
                type: 'text',
                placeholder: 'Street',
                name: 'address'

            },
            value: '',
            validation: {
                required: true
            },
            valid: false,
            touched: false
        },
        zipCode: {
            elementType: 'input',
            elementConfig: {
                type: 'text',
                placeholder: 'ZIP Code',
                name: 'zip'
            },
            value: '',
            validation: {
                required: true,
                minLength: 5,
                maxLength: 5,
                isNumeric: true
            },
            valid: false,
            touched: false
        },
        city: {
            elementType: 'input',
            elementConfig: {
                type: 'text',
                placeholder: 'City',
                name: 'city'
            },
            value: '',
            validation: {
                required: true
            },
            valid: false,
            touched: false
        },
        phone: {
            elementType: 'input',
            elementConfig: {
                type: 'text',
                placeholder: 'Phone Number',
                name: 'phone'
            },
            value: '',
            validation: {
                isNumeric: true
            },
            valid: false,
            touched: false
        },
        email: {
            elementType: 'input',
            elementConfig: {
                type: 'email',
                placeholder: 'Your E-Mail',
                name:'email'
            },
            value: '',
            validation: {
                required: true,
                isEmail: true
            },
            valid: false,
            touched: false
        },
        deliveryMethod: {
            elementType: 'select',
            elementConfig: {
                options: [
                    {value: 'fastest', displayValue: 'Fastest'},
                    {value: 'cheapest', displayValue: 'Cheapest'}
                ],
                name:'deliveryMethod'
            },
            value: '',
            validation: {},
            valid: true
        }
    }
  ) 
        const [formIsValid, setFormIsValid] = useState(false);
        const [loading, setLoading] = useState(false);
        const [isProcessing, setIsProcessing] = useState(false);
        const [checkoutError, setCheckoutError] = useState();


    const orderHandler = ( event ) => {
        console.log("orderHandler",props);
        event.preventDefault();
        setLoading(true);
        const formData = {};
        for (let formElementIdentifier in orderForm) {
            formData[formElementIdentifier] = orderForm[formElementIdentifier].value;
        }
        let updateDishes = {...props.dishes};
        Object.keys(updateDishes).forEach((key) => (updateDishes[key] === 0||null) && delete updateDishes[key]);
        console.log('dishes', updateDishes);
        const order = {
            dishes: updateDishes,
            price: props.totalPrice,
            orderData: formData
        }
        axios.post( '/orders.json', order )
            .then( response => {
                setLoading(false);
                props.history.replace( '/endpage' );
                console.log('props.match.path'+props.match.path)
            } )
            .catch( error => {
                setLoading(false);
            } );
    }

    const checkValidity = (value, rules) => {
        let isValid = true;
        if (!rules) {
            return true;
        }

        if (rules.required) {
            isValid = value.trim() !== '' && isValid;
        }

        if (rules.minLength) {
            isValid = value.length >= rules.minLength && isValid
        }

        if (rules.maxLength) {
            isValid = value.length <= rules.maxLength && isValid
        }

        if (rules.isEmail) {
            const pattern = /[a-z0-9!#$%&'*+/=?^_`{|}~-]+(?:\.[a-z0-9!#$%&'*+/=?^_`{|}~-]+)*@(?:[a-z0-9](?:[a-z0-9-]*[a-z0-9])?\.)+[a-z0-9](?:[a-z0-9-]*[a-z0-9])?/;
            isValid = pattern.test(value) && isValid
        }

        if (rules.isNumeric) {
            const pattern = /^\d+$/;
            isValid = pattern.test(value) && isValid
        }

        return isValid;
    }

   const inputChangedHandler = (event, inputIdentifier) => {
        const updatedOrderForm = {
            ...orderForm
        };
        const updatedFormElement = { 
            ...updatedOrderForm[inputIdentifier]
        };
        updatedFormElement.value = event.target.value;
        updatedFormElement.valid = checkValidity(updatedFormElement.value, updatedFormElement.validation);
        updatedFormElement.touched = true;
        updatedOrderForm[inputIdentifier] = updatedFormElement;

        let formIsValid = true;
        for (let inputIdentifier in updatedOrderForm) {
            console.log(updatedOrderForm[inputIdentifier],updatedOrderForm[inputIdentifier].valid);
            formIsValid = updatedOrderForm[inputIdentifier].valid && formIsValid;
        }
        console.log('formIsValid',formIsValid);
        setOrderForm(updatedOrderForm); 
        setFormIsValid(formIsValid);
    }





        const CardElementContainer = styled.div`
        height: 40px;
        display: flex;
        align-items: center;

        & .StripeElement {
        width: 100%;
        padding: 15px;
        }
    `;

       const stripe = useStripe();
       const elements = useElements();

       const handleFormSubmit = async ev => {
        ev.preventDefault();
        const billingDetails = {
            name: ev.target.name.value,
            email: ev.target.email.value,
            address: {
            city: ev.target.city.value,
            line1: ev.target.address.value,
            state: ev.target.state.value,
            postal_code: ev.target.zip.value
            }
        };
        const cardElement = elements.getElement(CardElement);
        console.log('cardElement',cardElement);
        const paymentMethodReq = await stripe.createPaymentMethod({
            type: 'card',
            card: cardElement,
            billing_details: billingDetails
        });
        console.log('paymentMethodReq',paymentMethodReq.paymentMethod.id);
        const payId = paymentMethodReq.paymentMethod.id;
        const res = await localServerAxios.post('http://localhost:8383/pay', {
        amount: 100, 
        billingDetails: billingDetails,
        paymentMethod: paymentMethodReq.paymentMethod,
        payId: payId
        });

        // setIsProcessing(true);
        const clientSecret = res.data['client_secret'];
        console.log('clientSecret',clientSecret);

        // console.log('billingDetails',billingDetails);

        // const result = await stripe.confirmCardPayment(clientSecret, {
        //     payment_method: {
        //       card: cardElement,
        //       billing_details: billingDetails
        //     },
        //   });
          const result = await stripe.confirmCardPayment(clientSecret, {
            payment_method: paymentMethodReq.paymentMethod.id
          });
          if (result.error) {
            // Show error to your customer (e.g., insufficient funds)
            console.log(result.error.message);
          } else {
            // The payment has been processed!
            if (result.paymentIntent.status === 'succeeded') {
              console.log('Money is in the bank!');
              // Show a success message to your customer
              // There's a risk of the customer closing the window before callback
              // execution. Set up a webhook or plugin to listen for the
              // payment_intent.succeeded event that handles any business critical
              // post-payment actions.
            }
          }

        };


        const cardElementOptions = {
            style: {
                base: {
                    fontSize: "16px",
                    color: "#fff",
                    "::placeholder": {
                        color: "#87bbfd"
                    }

                },
                invalid: {
                    color: '#FFC7EE',
                    iconColor: '#FFC7EE'
                }   
            },
            hidePostalCode: true,
        }




    const formElementsArray = [];
    for (let key in orderForm) {
        formElementsArray.push({
            id: key,
            config: orderForm[key]
        });
    }
    let form = (
        <div>
        <form onSubmit={handleFormSubmit}>
            {formElementsArray.map(formElement => (
                <Input 
                    key={formElement.id}
                    elementType={formElement.config.elementType}
                    elementConfig={formElement.config.elementConfig}
                    value={formElement.config.value}
                    invalid={!formElement.config.valid}
                    shouldValidate={formElement.config.validation}
                    touched={formElement.config.touched}
                    changed={(event) => inputChangedHandler(event, formElement.id)} />
            ))}
             <Row>
                    <CardElementContainer>
                    <CardElement options={cardElementOptions}/>
                    </CardElementContainer>
                </Row>
                <Row>
                    <SubmitButton disabled={isProcessing}>
                    {isProcessing ? "Processing..." : `Pay $${props.price}`}
                    </SubmitButton>
                </Row>
             </form>
             <Button btnType="Danger" clicked = {props.goBack}>BACK</Button>
            <Button btnType="Success" clicked = {orderHandler} disabled={!formIsValid}>ORDER</Button>

            {/* <Route
                path={props.match.path + 'endpage'}
                render={(props)=>(<EndPage
                    {...props} />)} 
            /> */}
           </div>

    );
    if ( loading ) {
        form = <Spinner />;
    }

        return (
            <div className={classes.ContactData}>
                <h4>Enter your Contact Data</h4>
                {form}
                {/* <form onSubmit={handleFormSubmit}>
                    <Row>
                        <BillingDetailsFields />
                    </Row>
                    <Row>
                        <CardElementContainer>
                        <CardElement options={cardElementOptions}/>
                        </CardElementContainer>
                    </Row>
                    {checkoutError && <CheckoutError>{checkoutError}</CheckoutError>}
                    <Row>
                        <SubmitButton disabled={isProcessing}>
                        {isProcessing ? "Processing..." : `Pay $${props.price}`}
                        </SubmitButton>
                    </Row>
                </form> */}
            </div>
        );
}


export default ContactData;
