import React from 'react';
import DishMenu from './DishMenu/DishMenu';
const DishMenus = (props) => {
    const controls = [
        {type: 'dish1'},
        {type: 'dish2'},
        {type: 'dish3'},
        {type: 'dish4'},
        {type: 'dish5'}  
    ]
    return (
        <div>
             {controls.map(ctr => (
                <DishMenu 
                key = {ctr.type}
                added = {()=>props.added(ctr.type)}
                indivdualDishNum = {props.dishes[ctr.type]}
                />
            ))}
        </div>
    )
}
export default DishMenus;
