import React, {useState}from 'react';
import DishMenus from './DishMenus/DishMenus';

const DISHPRICE = {
    dish1: 1,
    dish2: 2,
    dish3: 3,
    dish4: 4,
    dish5: 5
}
const MenuBoard = () => {
    let [menus, resetMenus] = useState(
        {
            dish1: 0,
            dish2: 0,
            dish3: 0,
            dish4: 0,
            dish5: 0
        }
    )
    let [totalPrice, resetTotalPrice] = useState(0)
    const addIngredients = (type) => {
        let oldCount = menus[type];
        let newCount = oldCount +1;
        let upDateMenus = {
            ...menus
        }
        upDateMenus[type] = newCount;
        let totalPrice = totalPrice + DISHPRICE[type];
        resetMenus(upDateMenus);
        resetTotalPrice(totalPrice);
    }
    return (
        <div>
            <DishMenus 
            added = {addIngredients}
            dishes = {menus} 
            />
        </div>
    )
}
export default MenuBoard;
