import React from 'react';
import classes from './Order.module.css';
const Order = (props) => {
    let ingredients = []
    for(let key in props.dishes) {
        ingredients.push(
            {
                key: key,
                name: key,
                num: props.ingredients[key]
            }
        )
    }
    let outPutIngredients = ingredients.map(
        ig => {
           return <span key={ig.key}>{ig.name} : {ig.num}</span>
        }
    )

    return (
            <div className = {classes.Order}>
                <div>{outPutIngredients}</div>
                <span>price: {props.price}</span>
            </div>
            );

}
export default Order;
